CREATE DATABASE GOWIII;
cardsUSE GOWIII;

CREATE TABLE Users(
id INT PRIMARY KEY AUTO_INCREMENT,
name VARCHAR(100),
email VARCHAR(100),
password VARCHAR(100));

CREATE TABLE Cards(
id INT PRIMARY KEY,
imagen VARCHAR(900),
title VARCHAR(100),
description VARCHAR(900));

CREATE TABLE Characters(
id INT PRIMARY KEY,
imagen VARCHAR(900),
title VARCHAR(100),
description VARCHAR(900));

DROP TABLE Cards;




